import React from 'react';
import { Movie } from '../types/movie';
import { VHSTape } from './VHSTape';

interface VHSShelfProps {
  movies: Movie[];
}

interface MoviesByGenre {
  [genre: string]: {
    movies: Movie[];
    collections: { [key: string]: Movie[] };
  };
}

export const VHSShelf: React.FC<VHSShelfProps> = ({ movies }) => {
  // Organize movies by genre and collections
  const organizedMovies = React.useMemo(() => {
    const moviesByGenre: MoviesByGenre = {};

    // First pass: organize movies by their primary genre
    movies.forEach(movie => {
      const primaryGenre = movie.genre[0]; // Use first genre as primary
      
      if (!moviesByGenre[primaryGenre]) {
        moviesByGenre[primaryGenre] = {
          movies: [],
          collections: {}
        };
      }

      if (movie.collection) {
        if (!moviesByGenre[primaryGenre].collections[movie.collection]) {
          moviesByGenre[primaryGenre].collections[movie.collection] = [];
        }
        moviesByGenre[primaryGenre].collections[movie.collection].push(movie);
      } else {
        moviesByGenre[primaryGenre].movies.push(movie);
      }
    });

    return moviesByGenre;
  }, [movies]);

  // Helper function to render a shelf row
  const renderShelfRow = (movies: Movie[]) => {
    return (
      <div className="grid grid-cols-6 gap-4">
        {movies.map((movie) => (
          <VHSTape key={movie.id} movie={movie} />
        ))}
      </div>
    );
  };

  if (movies.length === 0) {
    return (
      <div className="relative min-h-[200px]">
        <div className="absolute -left-8 -right-8 -bottom-4">
          <div className="h-8 bg-[#654321] shadow-inner" />
          <div className="h-4 bg-[#3d2813] rounded-b-lg" />
        </div>
        <div className="absolute inset-0 flex items-center justify-center">
          <p className="text-2xl text-purple-400 font-['Press_Start_2P'] text-center">
            Empty Shelf
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-24">
      {Object.entries(organizedMovies).map(([genre, { movies: genreMovies, collections }]) => (
        <div key={genre} className="space-y-24">
          {/* Genre Header */}
          <h2 className="text-3xl font-['Press_Start_2P'] text-purple-400 mb-8">
            {genre}
          </h2>

          {/* Collections first */}
          {Object.entries(collections).map(([collectionName, collectionMovies]) => (
            <div key={collectionName} className="relative">
              {/* Collection Label */}
              <div className="absolute -left-4 top-0 -translate-y-full mb-2">
                <span className="text-lg text-purple-300 font-['Press_Start_2P']">
                  {collectionName}
                </span>
              </div>

              {/* Wooden shelf */}
              <div className="absolute -left-8 -right-8 -bottom-4">
                <div className="absolute -top-4 left-0 right-0 h-4 bg-gradient-to-b from-transparent to-black/50" />
                <div className="h-8 bg-[url('data:image/png;base64,')] bg-repeat" />
                <div className="h-4 bg-[#3d2813] rounded-b-lg shadow-[inset_0_2px_4px_rgba(0,0,0,0.5)]" />
              </div>

              {/* VHS tapes */}
              <div className="relative pb-8">
                {renderShelfRow(collectionMovies)}
              </div>
            </div>
          ))}

          {/* Non-collection movies */}
          {genreMovies.length > 0 && (
            <div className="relative">
              {/* Wooden shelf */}
              <div className="absolute -left-8 -right-8 -bottom-4">
                <div className="absolute -top-4 left-0 right-0 h-4 bg-gradient-to-b from-transparent to-black/50" />
                <div className="h-8 bg-[url('data:image/png;base64,')] bg-repeat" />
                <div className="h-4 bg-[#3d2813] rounded-b-lg shadow-[inset_0_2px_4px_rgba(0,0,0,0.5)]" />
              </div>

              {/* VHS tapes */}
              <div className="relative pb-8">
                {renderShelfRow(genreMovies)}
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};